mapper.update:

update tb_merchdown
set merchuid = #{merchuid,jdbcType=VARCHAR},
  merchscore = #{merchscore,jdbcType=INTEGER},
  downdatetime = #{downdatetime,jdbcType=TIMESTAMP}
where uid = #{uid,jdbcType=VARCHAR}

mapper.select:
select

from tb_merchdown

  where merchuid = (
  select uid from tb_merch where merchname LIKE concat(concat('%',#{merchname}),'%')
  )

LIMIT #{offset},#{limit}
